<div class="box box-primary">
            <div class="box-header with-border">
              <h3 class="box-title">Chofer</h3>
            </div>
            <!-- /.box-header -->
            <!-- form start -->
             <!--role="form"-->
            <form>
              <div class="box-body">
                <div class="alert alert-danger hide"></div>
                <div class="alert alert-success hide"></div>
                <div class="form-group">
                  <label for="rif_driver"><strong>Cedula</strong></label>
                  <div class="input-group">
                    <div class="input-group-addon">
                      <i class="fa fa-id-card"></i>
                    </div>
                    <input type="text" class="form-control" id="rif_driver" placeholder="Cedula del chofer">
                  </div>
                </div>

                <div class="form-group">
                  <label for="name_driver"><strong>Nombre</strong></label>
                  <div class="input-group">
                    <div class="input-group-addon">
                      <i class="fa fa-child"></i>
                    </div>
                    <input type="text" class="form-control" id="name_driver" placeholder="Nombre del chofer">
                  </div>
                </div>

                <div class="form-group">
                  <label for="phone_driver"><strong>Telefono</strong></label>
                  <div class="input-group">
                    <div class="input-group-addon">
                      <i class="fa fa-phone"></i>
                    </div>
                    <input type="text" class="form-control" id="phone_driver" placeholder="Telefono del chofer">
                  </div>
                </div> 
              </div>
              <!-- /.box-body -->
              <div class="box-footer">
                <button type="button" class="btn btn-primary" id="save_driver">Guardar</button>
              </div>
            </form>
          </div>